// AllSalarySlugs returns all job slugs.
func AllSalarySlugs() []string {
	slugs := make([]string, 0, len(Salaries))
	for slug := range Salaries {
		slugs = append(slugs, slug)
	}
	for i := 0; i < len(slugs); i++ {
		for j := i + 1; j < len(slugs); j++ {
			if slugs[i] > slugs[j] {
				slugs[i], slugs[j] = slugs[j], slugs[i]
			}
		}
	}
	return slugs
}

// GetSalary returns salary data by slug, or nil if not found.
func GetSalary(slug string) *SalaryData {
	if s, ok := Salaries[slug]; ok {
		return &s
	}
	return nil
}
